import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'

import { viewTicket } from '../../Redux/action'
import Navbar from '../Common/Navbar'

class Pagination extends Component {
    constructor(props) {
        super(props)

        this.state = {
            priority: '',
            status: ''
        }
    }

    componentDidMount = () => {
        const { loginSuccess, viewTicket } = this.props
        viewTicket(loginSuccess.id, 2, 5)
    }

    render() {
        const { view_ticket, viewTicket,loginSuccess } = this.props
        console.log(view_ticket)
        let arr = []

        for(let i=1;i<=view_ticket.total_pages;i++)
            arr.push(i)

        return (
            <>
                <nav className="d-flex justify-content-center mt-5">
                    <ul class="pagination">
                        <li class="page-item disabled">
                            <button>Prev</button>
                        </li>
                        {
                            arr.map(item=> <li className="page-item"><button onClick={()=>viewTicket()}>{item}</button></li>)
                        }
                        <li class="page-item">
                            <button>Next</button>
                        </li>
                    </ul>
                </nav>
            </>
        )
    }
}

const mapStateToProps = state => ({
    view_ticket: state.view_ticket,
    loginSuccess: state.loginSuccess
});
const mapDispatchToProps = dispatch => ({
    viewTicket: payload => dispatch(viewTicket(payload))
});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Pagination);
