import React, { Component } from 'react'
import { connect } from 'react-redux'
import Navbar from '../Common/Navbar'
import CreateTicket from '../Ticket/CreateTicket'

class ChangeStatus extends Component {
    constructor(props) {
        super(props)

        this.state = {
            status: this.props.view_ticket
        }
    }

    componentDidUpdate = () => {
        const { view_ticket, match } = this.props

        const data = view_ticket.find(item => item.id === Number(match.params.id))
        this.setState({ status: data.status })
    }

    render() {
        const { view_ticket, match } = this.props

        const data = view_ticket.find(item => item.id === Number(match.params.id))
        console.log(this.state.status)
        return (
            <>
                <Navbar />
                <div className="container">
                    <div className="card text-white bg-info mb-3" style={{ maxWidth: "25rem", marginTop: "150px" }}>
                        <h5 className="card-header">Ticket :{data.title}</h5>
                        <div className="card-body">
                            <p className="card-title">Cagegory :{data.category}</p>
                            <p className="card-text">Priority :{data.priority}</p>

                            <label className="my-1 mr-2 lead">Status :{data.status}</label>
                            <select className="form-control my-1 mr-sm-2"
                                name="status"
                                value={this.state.status}
                                onChange={(e) => this.setState({ status: e.target.value })}
                            >
                                <option>Choose..</option>
                                <option value="assign">Assign</option>
                                <option value="in_process">In Process</option>
                                <option value="closed">Closed</option>
                            </select>

                        </div>
                    </div>
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    view_ticket: state.view_ticket
});
const mapDispatchToProps = dispatch => ({

});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ChangeStatus);
