import {
    LOGIN,
    SIGNUP
} from './actionTypes'
import Login from '../Component/Auth/Login'

export const initState = {
    loginSuccess:false
}

export default (state = initState, { type, payload }) => {
    console.log(payload)
    switch (type) {
        case Login:
            if(payload.authtoken)
                return{...state,loginSuccess:payload}
            else
                alert(payload.message)
        default:
            return state
    }
}
