import {
    LOGIN,
    SIGNUP
} from './actionTypes'
import { loginSuccess } from './action'


export const initState = {
    loginSuccess:[]
}

export default (state = initState, { type, payload }) => {
    console.log(payload)

    switch (type) {
        case LOGIN:
            if(payload.message==='logged_in')
                return{
                    ...state,
                    loginSuccess:payload
                }
            else
                alert

        default:
            return state
    }
}
