import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { viewTicket } from '../../Redux/action'
import Navbar from '../Common/Navbar'

class Pagination extends Component {
    constructor(props) {
        super(props)

        this.state = {
            priority: '',
            status: '',
            curr_page:''
        }
    }

    componentDidMount = () => {
        const { loginSuccess, viewTicket } = this.props
        viewTicket(loginSuccess.id, 1, 5)
    }

    handlePagination = (id) => {
        this.setState({
            curr_page:id
        })
    }

    render() {
        const { view_ticket, viewTicket,loginSuccess } = this.props
        let {curr_page} = this.state
        // console.log(view_ticket)
        let arr = []

        for(let i=1;i<=view_ticket.total_pages;i++)
            arr.push(i)

        return (
            <>
                <nav className="d-flex justify-content-center mt-5">
                    <ul class="pagination">
                        <li class="page-item disabled">
                            <button>Prev</button>
                        </li>
                        {
                            arr.map(item=> <li className="page-item"><Link to={`/companyDashboard/page/${item}`}>
                                <button onClick={()=>this.handlePagination(item) ,viewTicket(loginSuccess.id,item,5)}>{item}</button>
                                </Link></li>)
                        }
                        <li class="page-item">
                            <button id="next" onClick={()=>this.handlePagination(curr_page+1) ,viewTicket(loginSuccess.id,curr_page,5)}>Next</button>
                        </li>
                    </ul>
                </nav>
            </>
        )
    }
}

const mapStateToProps = state => ({
    view_ticket: state.view_ticket,
    loginSuccess: state.loginSuccess
});
const mapDispatchToProps = dispatch => ({
    viewTicket: (payload,page,per_page) => dispatch(viewTicket(payload,page,per_page))
});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Pagination);
