import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect} from 'react-router-dom'

class Login extends Component {
    constructor(props) {
        super(props)

        this.state = {
            username: '',
            password: '',
            role: ''
        }
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <>
                <div className="container">
                    <h3 className="text-center m-3">Login</h3>

                    <div className="border row offset-3 col-md-6 p-3 shadow-lg p-3 mb-5 bg-white rounded rounded">
                        <div className='col-12'>
                            <div className="form-group col-12">
                                <label>Username</label>
                                <input className="form-control"
                                    name="username"
                                    value={this.state.username}
                                    onChange={this.handleChange} 
                                    placeholder="username or gmail"/>
                            </div>
                            <div className="form-group col-12">
                                <label>Password</label>
                                <input className="form-control"
                                    name="password"
                                    value={this.state.password}
                                    onChange={this.handleChange} 
                                    placeholder="password"/>
                            </div>
                            <div className="form-group col-md-4">
                                <label >Role</label>
                                <select className="form-control"
                                    name="role"
                                    value={this.state.role}
                                    onChange={this.handleChange}
                                >
                                    <option >Role</option>
                                    <option value="company">Company</option>
                                    <option value="user">User</option>
                                </select>
                            </div>
                        </div>

                        <button className="btn btn-info ml-5 mt-3"
                            onClick={(e) => {
                                e.preventDefault()

                            }}
                        >
                            Login
                        </button>
                        <p className="m-3 ml-5 pl-5">Not have an account ?</p>
                        <button className="btn btn-info ml-5 mt-3">
                            Login
                        </button>
                    </div>
                </div>
            </>
        )
    }
}
