import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { viewTicket } from '../../Redux/action'
import Navbar from '../Common/Navbar'

class Pagination extends Component {
    constructor(props) {
        super(props)

        this.state = {
            priority: '',
            status: '',
            curr_page: 1,
            flag: false
        }
    }

    componentDidMount = () => {
        const { loginSuccess, viewTicket } = this.props
        viewTicket(loginSuccess.id, 1, 5)
    }

    handlePagination = (id) => {
        const { viewTicket, view_ticket, loginSuccess } = this.props
        id = Number(id)

        if (id <= view_ticket.total_pages && id > 0)
            viewTicket(loginSuccess.id, id, 5)

        this.setState({
            curr_page: id
        })
    }

    render() {
        const { view_ticket, viewTicket, loginSuccess } = this.props
        let { curr_page, flag } = this.state
        // console.log(view_ticket)
        let arr = []

        for (let i = 1; i <= view_ticket.total_pages; i++)
            arr.push(i)

        return (
            <>
                <nav className="d-flex justify-content-center mt-5">
                    <ul class="pagination">
                        <li class="page-item disabled">
                            <Link to={`/companyDashboard/page/${curr_page >1 ? curr_page - 1:1}`}><button id="next" onClick={() => this.handlePagination(curr_page - 1)}>Prev</button></Link>
                        </li>
                        {
                            arr.map(item => <li className="page-item"><Link to={`/companyDashboard/page/${item}`}>
                                <button onClick={() => this.handlePagination(item)}>{item}</button>
                            </Link></li>)
                        }
                        <li class="page-item">
                            <Link to={`/companyDashboard/page/${curr_page < view_ticket.total_pages ?curr_page + 1: arr[arr.length()-1]}`}><button id="next" onClick={() => this.handlePagination(curr_page + 1)}>Next</button></Link>
                        </li>
                    </ul>
                </nav>
            </>
        )
    }
}

const mapStateToProps = state => ({
    view_ticket: state.view_ticket,
    loginSuccess: state.loginSuccess
});
const mapDispatchToProps = dispatch => ({
    viewTicket: (payload, page, per_page) => dispatch(viewTicket(payload, page, per_page))
});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(Pagination);
