import React, { Component } from 'react'
import { connect } from 'react-redux'
import Navbar from '../Common/Navbar'
import CreateTicket from '../Ticket/CreateTicket'

class ChangeStatus extends Component {
    constructor(props) {
        super(props)

        this.state = {
            status: ''
        }
    }

    render() {
        const { view_ticket, match } = this.props

        const data = view_ticket.find(item => item.id === Number(match.params.id))
        console.log(data)
        return (
            <>
                <Navbar />
                <div className="container">
                    <div class="card text-white bg-info mb-3" style="max-width: 18rem;">
                        <div class="card-header">{data.title}</div>
                        <div class="card-body">
                            <h5 class="card-title">{data.category}</h5>
                            <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>

                            <label className="my-1 mr-2 lead ml-4">Status</label>
                            <select className="form-control my-1 mr-sm-2" >
                                <option >Choose...</option>
                                <option value="assign">Assign</option>
                                <option value="in_process">In Process</option>
                                <option value="closed">Closed</option>
                            </select>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    view_ticket: state.view_ticket
});
const mapDispatchToProps = dispatch => ({

});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ChangeStatus);
