import {
  LOGIN,
  SIGNUP  
} from './actionTypes'

import axios from 'axios'

///// login user or company //////////
export const loginSuccess = payload =>({
    type:LOGIN,
    payload
})

export const login = payload => dispatch => {
    console.log(payload)

    axios.post('http://127.0.0.1:5000/login',payload)
    .then(res => res.data)
    .then(res => dispatch(loginSuccess(res)))
}
