import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect, Link } from 'react-router-dom'
import { companyDetails } from '../../Redux/action'

class UserDashboard extends Component {
    constructor(props) {
        super(props)

        this.state = {
            title: "",
            category: "",
            priority: "",
            company: "",
            user_id
        }
    }

    componentDidMount = () => {
        const { companyDetails } = this.props
        companyDetails()
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        const { companyData } = this.props
        console.log(companyData)
        return (
            <>
                <div className="container" style={{ marginTop: "150px" }}>
                    <div class="card shadow p-3 mb-5 bg-white rounded" style={{ width: "30rem" }}>
                        <div class="card-header">
                            Add Ticket
                        </div>
                        <div class="card-body">
                            <div className="form-group col-12">
                                <label>Title</label>
                                <input className="form-control"
                                    name="title"
                                    value={this.state.title}
                                    onChange={this.handleChange}
                                    placeholder="title" />
                            </div>
                            <div className='row pl-3'>
                                <div className="form-group col-md-5">
                                    <label >category</label>
                                    <select className="form-control"
                                        name="category"
                                        value={this.state.category}
                                        onChange={this.handleChange}
                                    >
                                        <option >Category</option>
                                        <option value="hardware">Hardware</option>
                                        <option value="software">Software</option>
                                        <option value="application">Application</option>
                                    </select>
                                </div>
                                <div className="form-group col-md-5">
                                    <label >Proiroty</label>
                                    <select className="form-control"
                                        name="proiroty"
                                        value={this.state.proiroty}
                                        onChange={this.handleChange}
                                    >
                                        <option >Proiroty</option>
                                        <option value="1">1</option>
                                        <option value="2">2</option>
                                        <option value="3">3</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-group col-md-5">
                                <label >Company Name</label>
                                <select className="form-control"
                                    name="company"
                                    value={this.state.company}
                                    onChange={this.handleChange}
                                >
                                    <option >Company</option>
                                    {
                                        companyData && companyData.map(ele =>
                                            <>
                                                <option value={ele.id}>{ele.name}</option>
                                            </>
                                        )
                                    }
                                </select>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    companyData: state.companyData
});
const mapDispatchToProps = dispatch => ({
    companyDetails: payload => dispatch(companyDetails(payload))
});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(UserDashboard);
