import {
    LOGIN,
    SIGNUP,
    COMPANY_DETAILS,
    ADD_TICKET,
    TICKET_REPORT,
    LOGOUT,
    VIEW_TICKET
} from './actionTypes'
import { loginSuccess } from './action'


export const initState = {
    loginSuccess: [],
    companyData: [],
    ticketMsg:[],
    allUserTicket:[],
    VIEW_TICKET
}

export default (state = initState, { type, payload }) => {
    console.log(payload)

    switch (type) {
        case LOGIN:
            if (payload.message === 'logged_in')
                return {
                    ...state,
                    loginSuccess: payload
                }
            else
                alert(payload.message)
                break
        case COMPANY_DETAILS:
            return {
                ...state,
                companyData: payload.data
            }
