            return {
                ...state,
                ticketMsg:payload
            }
        case TICKET_REPORT:
            return {
                ...state,
                allUserTicket:payload.data
            }
        case LOGOUT:
            return {
                ...state,
                loginSuccess:[]
            }
        case VIEW_TICKET:
            return {
                ...state,

            }
        default:
            return state
    }
}
