import React, { Component } from 'react'
import { connect } from 'react-redux'
import { } from '../../Redux/action'
import uuidv4 from 'uuid'
import Navbar from '../Common/Navbar'
import { Redirect } from 'react-router-dom'

class ViewTicket extends Component {
    constructor(props) {
        super(props)

    }

    render() {
        const { allUserTicket, match, companyData } = this.props

        const data = allUserTicket.find(item => item.id === Number(match.params.id))
        // const company = companyData.find(item => item.id === Number(data.company_id))

        console.log(data)

        return (
            <>
                <Navbar />
                <div className="container" style={{ marginTop: "150px" }}>
                    <div className="row">
                        <div className="offset-3 col-6">
                            <div className="card border-info mb-3">
                                <div className="card-header">Ticket Information</div>
                                <div className="card-body text-info">
                                    <h5 className="card-title">{data.title}</h5>
                                    <p className="card-text">Company Name : {data.company} </p>
                                    <span>
                                        <span className="card-text">Status: {data.status}</span>
                                        <span className="card-text">Priority :{data.priority}</span>
                                        <span className="card-text">Category :{data.category}</span>
                                    </span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    allUserTicket: state.allUserTicket,
    companyData: state.companyData
});
const mapDispatchToProps = dispatch => ({

});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ViewTicket);
