import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect, Link } from 'react-router-dom'
import { uuidv4 } from 'uuid'
import { viewTicket } from '../../Redux/action'
import Navbar from '../Common/Navbar'

class CompanyDashboard extends Component {

    componentDidMount = () => {
        const { loginSuccess, viewTicket } = this.props
        viewTicket(loginSuccess.id)
    }

    render() {
        const { view_ticket } = this.props
        console.log(view_ticket[0])

        return (
            <>
                <Navbar />
                <div className="container" style={{ marginTop: "150px" }}>
                    <div className="table-responsive-sm">
                        <table className="table">
                            <thead>
                                <tr className="table-info">
                                    <th scope="col">Id</th>
                                    <th scope="col">Title</th>
                                    <th scope="col">Category</th>
                                    <th scope="col">Status</th>
                                    <th>Resolve</th>
                                </tr>
                            </thead>
                            <tbody>
                                {view_ticket && view_ticket.map(ele =>

                                    <tr className='table-success'>
                                        <th scope="row">{ele.id}</th>
                                        <td className='text-danger'>{ele.title}</td>
                                        <td>{ele.category}</td>
                                        <td>{ele.status}</td>
                                        <td><Link to='/change_status/'>change status</Link></td>
                                    </tr>

                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    view_ticket: state.view_ticket,
    loginSuccess: state.loginSuccess
});
const mapDispatchToProps = dispatch => ({
    viewTicket: payload => dispatch(viewTicket(payload))
});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(CompanyDashboard);
