import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect, Link } from 'react-router-dom'
import { uuidv4 } from 'uuid'
import { viewTicket } from '../../Redux/action'

class CompanyDashboard extends Component {
    constructor(props) {
        super(props)

        this.state = {
            email: '',
            password: '',
            role: ''
        }
    }

    componentDidMount = () => {
        const { loginSuccess, viewTicket } = this.props
        viewTicket(loginSuccess.id)
    }

    render() {
        const { view_ticket } = this.props
        console.log(view_ticket)
        return (
            <>
                <div className="container">
                    <div className="container" style={{ marginTop: "150px" }}>
                        <div className="table-responsive-sm">
                            <table className="table">
                                <thead>
                                    <tr className="table-info">
                                        <th scope="col">Id</th>
                                        <th scope="col">Title</th>
                                        <th scope="col">Category</th>
                                        <th scope="col">Company</th>
                                        <th scope="col">Status</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        view_ticket && view_ticket.map(ele => (
                                            <>
                                                <tr key={uuidv4()} className="table-success">
                                                    <td>{ele.id}</td>
                                                    <td className='text-danger'>{ele.title}</td>
                                                    <td>{ele.category}</td>
                                                    <td>{ele.company}</td>
                                                    <td>{ele.status}</td>
                                                </tr>
                                            </>
                                        ))
                                    }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    view_ticket: state.view_ticket,
    loginSuccess:state.login
});
const mapDispatchToProps = dispatch => ({
    viewTicket: payload => dispatch(viewTicket(payload))
});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(CompanyDashboard);
