import React, { Component } from 'react'
import { connect } from 'react-redux'
import { } from '../../Redux/action'
import uuidv4 from 'uuid'
import Navbar from '../Common/Navbar'
import { Redirect } from 'react-router-dom'

class ViewTicket extends Component {
    constructor(props) {
        super(props)

    }

    render() {
        const { allUserTicket, match, companyData } = this.props

        const data = allUserTicket.find(item => item.id === Number(match.params.id))
        // const company = companyData.find(item => item.id === Number(data.company_id))

        console.log(data)

        return (
            <>
                <Navbar />
                <div className="container" style={{ marginTop: "150px" }}>
                    <div className="row">
                        <div className="offset-2 col-8">
                            <div className="card border-info mb-3">
                                <div className="card-header">Ticket Information</div>
                                <div className="card-body lead">
                                    <h5 className="card-title">{data.title}</h5>
                                    <p className="card-text">Company Name : {data.company} </p>
                                    <span>
                                        <span className="card-text pr-3">Status : {data.status}</span>
                                        <span className="card-text pr-3">Category : {data.category}</span>
                                    </span>
                                </div>
                            <div className="m-4 p-2 bg-dark">
                                <div className="border border-secondary float-right" style={{width:"300px"}}>
                                    <div className="m-3">
                                        <strong className="">Bootstrap</strong>
                                        <small className='float-right text-muted'>11 mins ago</small>
                                    </div>
                                    <div classtyle={{backgroundColor:"#ECEFF1"}}>
                                        Hello, world! This is a toast message.
                                    </div>
                                </div>
                            </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

const mapStateToProps = state => ({
    allUserTicket: state.allUserTicket,
    companyData: state.companyData
});
const mapDispatchToProps = dispatch => ({

});
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(ViewTicket);
